import { useState } from "react";

function App() {
    const [text, setText] = useState('Привет');

    const handleClick = () => {
        setText(text === 'Привет' ? 'Пока' : 'Привет');
    };


  return (
    <div>
      <p>{text}</p>
      <button onClick={handleClick}>Переключить</button>
    </div>
  );
}

export default App;
